export default `.control{display:flex;flex-direction:column;margin:var(--space-small) 0;width:100%}.control label{font-size:var(--font-4);font-weight:700}.control input,.control select{padding:var(--space-small) var(--space);border:1px solid var(--button)}.control button{cursor:pointer;padding:var(--space-small) var(--space);border:0;background-color:var(--button);color:#fff}.control button:hover{background-color:var(--button-hover)}.buttons{margin-top:var(--space);display:flex;flex-direction:row;justify-content:flex-end}.buttons:has(button:nth-child(2)){justify-content:space-between}.content-wrapper{display:flex;flex:1;max-height:calc(100dvh - 245px)}.content-wrapper>*{flex:1 1 0;width:0}@media screen and (max-width: 768px){.content-wrapper{display:block}.card-wrapper,app-file-list{width:100%}.card-wrapper.card-wrapper{grid-template-columns:1fr}}input[type=file]{display:none}.card-wrapper{display:grid;grid-template-columns:repeat(auto-fit,minmax(260px,1fr));gap:var(--space);padding:var(--space);max-height:calc(100% - 72px);overflow:auto;align-items:self-start}@media screen and (max-width: var(--mobile-breakpoint)){.card-wrapper{display:block;width:100%}app-file-list{width:100%}}.card{cursor:pointer;display:grid;grid-template-columns:repeat(auto-fit,minmax(260px,1fr));padding:var(--space);border-radius:6px;border:1px solid var(--default-color);text-align:center;line-height:1.2;transition:box-shadow .3s ease-in-out;max-height:250px}.card ul{list-style-type:none;margin:0;padding:0}.card:hover{box-shadow:0 0 10px var(--default-color-dark)}.icon{z-index:5;background-color:var(--default-color);border-radius:50%;width:60px;height:60px;cursor:pointer}.icon svg{fill:#fff}.button{display:inline-flex;align-items:center;justify-content:center;padding:8px 16px;font-family:Roboto,sans-serif;font-size:16px;font-weight:500;border-radius:6px;border:none;cursor:pointer;transition:background-color .2s,transform .1s;margin:0 var(--space-small)}.button-primary{background-color:#0b6357;color:#fff}.button-primary:hover{background-color:#139987;transform:scale(1.05)}.button-primary:focus{outline:2px solid #073b33;outline-offset:2px}.button-primary:disabled{background-color:#d1d5db;opacity:.6;cursor:not-allowed}.button-secondary{background-color:transparent;border:1px solid #0b6357;color:#0b6357}.button-secondary:hover{background-color:#e6efed}.button-secondary:focus{outline:2px solid #0b6357;outline-offset:2px}.btn{padding:8px 16px;border:none;border-radius:4px;cursor:pointer}.btn-primary{background:#007bff;color:#fff}.btn-secondary{background:#6c757d;color:#fff}h1{text-align:center}h1,h2{margin:var(--space-small) 0}.floating-icon{position:fixed;bottom:var(--space);right:var(--space);z-index:1000;border-radius:50%;background-color:var(--default-color);width:60px;height:60px;cursor:pointer;border:none;margin:0;padding:var(--space);box-shadow:0 8px 20px #00000040;transition:transform .2s ease,box-shadow .2s ease}.floating-icon:focus-visible{outline:2px solid var(--button-hover);outline-offset:2px}.floating-icon:hover{transform:scale(1.05);box-shadow:0 12px 24px #0000004d}.upload-icon{bottom:var(--space);left:var(--space);right:auto}.add-icon{bottom:var(--space);right:var(--space);left:auto}.add-icon i{display:flex;height:100%;width:100%;align-items:center;justify-content:center}@media (max-width: 768px){.floating-icon{width:52px;height:52px;padding:var(--space-small, 8px);bottom:var(--space-small, 8px);right:var(--space-small, 8px)}.upload-icon{left:var(--space-small, 8px)}}:root{--space-tiny: 4px;--space-small: 8px;--space: 16px;--space-large: 24px;--space-xlarge: 32px;--font-1: 21px;--font-2: 18px;--font-3: 16px;--font-4: 14px;--font-5: 11px;--default-color: #0b6357;--default-color-dark: #00120f;--button: #0b6357;--button-hover: #138172;--mobile-breakpoint: 768px}html,body{margin:0;padding:0;font-family:system-ui,-apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Oxygen,Ubuntu,Cantarell,Open Sans,Helvetica Neue,sans-serif}*,*:after,*:before{box-sizing:border-box}
`;